import styled from 'styled-components';

const Header = styled.header`
    height: 60px;
    position: fixed;
    left: 0;
    top: 0;
    display: flex;
    width: 100%;
    align-items: center;
    justify-content: space-between;
    padding: 10px 25px;
    .input_wrap {
        max-width: 250px;
        width: 100%;
        border-radius: 4px;
        display: flex;
        align-items: center;
        position: relative;
        .input {
          border: none;
        }
        .icon {
          position: absolute;
          right: 0;
        }
    }
`

export {
    Header
}
