import styled from 'styled-components';

const Header = styled.header`
    height: 60px;
    position: fixed;
    left: 0;
    top: 0;
    display: flex;
    width: 100%;
    align-items: center;
    justify-content: space-between;
    padding: 10px 25px;
    .input_wrap {
        max-width: 250px;
        width: 100%;
        border-radius: 2px;
        display: flex;
        align-items: center;
        position: relative;
        border: 1px solid #000;
        padding: 5px 10px;
        .input {
          border: none;
        }
        .icon {
          position: absolute;
          right: 0;
          width: 20px;
          fill: grey;
          height: 20px;
          top: 50%;
          transform: translateY(-50%);
        }
    }
`

export {
    Header
}
