import React, {useState} from "react";

import { ProductItemWrap, Info, FullScreenImage } from "./styled";

import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import SearchIcon from "@mui/icons-material/Search";
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';

const ProductItem = ({ data }) => {
  const [ ShowFullImage, setShowFullImage ] = useState(false)
  console.log(ShowFullImage)
  return (
    <>
      <ProductItemWrap>
      <div className={ 'circle' } />
      <img src={ data.img } className={ 'product-image' } alt="product" />

      <Info>
        <div className={ 'icon' }><AddShoppingCartIcon /></div>
        <button onClick={ ()=> setShowFullImage(true) } className={ 'icon' }><SearchIcon /></button>
        <div className={ 'icon' }><FavoriteBorderIcon /></div>
      </Info>
      
      </ProductItemWrap>

      { ShowFullImage && (
        <FullScreenImage>
        <img src={ data.img } />
      </FullScreenImage>
      ) }
    </>
  )
};

export default ProductItem;
