import styled from 'styled-components';

const ProductsWrap = styled.div`
    
    .product-section {
    padding: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    background-color: #fbf0f4;
    }
`

export {
    ProductsWrap
}
