import styled from "styled-components";

const ProductItemWrap = styled.div`
  flex: 1;
  margin: 5px;
  min-width: 280px;
  height: 350px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f5fbfd;
  position: relative;

.circle {
  width: 200px;
  height: 200px;
  border-radius: 50%;
  background-color: white;
  position: absolute;
}

.product-image {
  height: 75%;
  z-index: 2;
}

`;

export { ProductItemWrap };
